import React, { useState, useEffect } from 'react';
import { Link as RouterLink, withRouter, useHistory, useParams } from 'react-router-dom';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import {
	Card,
	CardHeader,
	CardContent,
	CardActions,
	Divider,
	Grid,
	Button,
	TextField,
	Modal
} from '@material-ui/core';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import ComponentService from '../ComponentService'


function getModalStyle() {
	const top = 50;
	const left = 50;

	return {
		top: `${top}%`,
		left: `${left}%`,
		transform: `translate(-${top}%, -${left}%)`,
	};
}

const useStyles = makeStyles((theme) => ({
	root: {},
	btn: {
		background: '#5E9A78',
		color: '#FFFFFF',
		marginTop: '10px'
	},
	paper: {
		position: 'absolute',
		display: 'flex',
		flexDirection: 'column',
		justifyContent: 'center',
		alignItems: 'center',
		width: 400,
		backgroundColor: theme.palette.background.paper,
		border: 'none',
		padding: theme.spacing(2, 4, 3),
	},
	success: {
		fontSize: '80px',
		margin: '0 0 20px'
	}
}));

const PengadaanDetail = props => {
	const history = useHistory();
	const { className, ...rest } = props;

	const [values, setValues] = useState({});
	const [openModal, setOpenModal] = useState(false)
	const [modalStyle] = useState(getModalStyle);

	const [pengadaan, setPengadaan] = useState({});

	const { id } = useParams();

	useEffect(() => {
		refreshPengadaan(id)
	})

	const refreshPengadaan = id => {
		ComponentService.detailPengadaan(id).then(response => setPengadaan(response.data))
	}

	const classes = useStyles();

	const handleChange = event => {
		setValues({
			...values,
			[event.target.name]: event.target.value
		});
	};

	const handleSubmit = event => {
		event.preventDefault();

		const pengadaan = values

		ComponentService.insertPengadaan(pengadaan).then(response => setOpenModal(true))

	}

	const handleClose = () => {
		setOpenModal(false)
	}

	const statusOption = () => {
		if (pengadaan.status === 0) {
			return "Usulan Pengguna"
		} else if (pengadaan.status === 1) {
			return "Menunggu Persetujuan"
		} else if (pengadaan.status === 2) {
			return "Ditolak"
		} else {
			return "Disetujui"
		}
	}

	const body = (
		<div style={modalStyle} className={classes.paper}>
			<CheckCircleOutlineIcon style={{ color: '#6C987B' }} id="modal-logo" className={classes.success} />
			<p id="modal-description">
				Data pengadaan berhasil ditambahkan
			</p>
			<RouterLink to='/pengadaan'>
				<Button
					className={classes.btn}
					variant="contained"
				>
					Oke
					</Button>
			</RouterLink>
		</div>
	)

	return (
		<Card
			{...rest}
			className={clsx(classes.root, className)}
		>
			<Modal
				open={openModal}
				onClose={handleClose}
				aria-labelledby="modal-logo"
				aria-describedby="modal-description"
			>
				{body}
			</Modal>
			<form
				autoComplete="off"
				noValidate
			>
				<CardHeader
					title="Data Detail Pengadaan"
				/>
				<Divider />
				<CardContent>
					<Grid
						container
						spacing={3}
					>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Nama"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="nama"
								onChange={handleChange}
								variant="outlined"
								disabled="true"
								value={pengadaan.nama}
							/>
						</Grid>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Judul Buku"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="judul"
								onChange={handleChange}
								variant="outlined"
								disabled="true"
								value={pengadaan.judul}
							/>
						</Grid>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Pengarang"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="pengarang"
								onChange={handleChange}
								variant="outlined"
								disabled="true"
								value={pengadaan.pengarang}
							/>
						</Grid>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Penerbit"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="penerbit"
								onChange={handleChange}
								variant="outlined"
								disabled="true"
								value={pengadaan.penerbit}
							/>
						</Grid>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Jumlah Buku"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="jumlah"
								onChange={handleChange}
								type="number"
								variant="outlined"
								disabled="true"
								value={pengadaan.jumlah}
							/>
						</Grid>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Harga Buku"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="harga"
								onChange={handleChange}
								type="number"
								variant="outlined"
								disabled="true"
								value={pengadaan.harga}
							/>
						</Grid>
						<Grid
							item
							md={12}
							xs={12}
						>
							<TextField
								fullWidth
								label="Status"
								InputLabelProps={{ shrink: true }}
								margin="dense"
								name="status"
								onChange={handleChange}
								variant="outlined"
								disabled="true"
								value={statusOption}
							/>
						</Grid>
					</Grid>
				</CardContent>
				<Divider />
			</form>
		</Card>
	);
};

PengadaanDetail.propTypes = {
	className: PropTypes.string
};

export default PengadaanDetail;
